xwikidoc.content:
{{groovy}}
def committerDocs = services.query.xwql("from doc.object(GitHubStats.CommitterClass) as committer").execute()
if (committerDocs.size() > 0) {
  println "|=Avatar|=Id|=Repositories|=Aliases"
  committerDocs.each() { docName ->
    def committerDoc = xwiki.getDocument(docName)
    def committerObject = committerDoc.getObject("GitHubStats.CommitterClass")
    def avatar = committerObject.getProperty("avatar").value
    def id = committerObject.getProperty("id").value
    def repositories = committerObject.getProperty("repositories").value
    def aliases = committerObject.getProperty("aliases").value
    println "|[[image:${avatar}]]|[[${id}>>${docName}]]|${repositories.join(', ')}|${aliases.join(', ')}"
  }
}
{{/groovy}}
