xwikidoc.content:
{{groovy}}
def repoDocs = services.query.xwql("from doc.object(GitHubStats.RepositoryClass) as repo").execute()
if (repoDocs.size() > 0) {
  println "|=Organization|=Id|=Name|=Git URL|=HTML URL"
  repoDocs.each() { docName ->
    def repoDoc = xwiki.getDocument(docName)
    def repoObject = repoDoc.getObject("GitHubStats.RepositoryClass")
    def organization = repoObject.getProperty("organization").value
    def id = repoObject.getProperty("id").value
    def name = repoObject.getProperty("name").value
    def giturl = repoObject.getProperty("giturl").value
    def htmlurl = repoObject.getProperty("htmlurl").value
    println "|${organization}|[[${id}>>${docName}]]|${name}|${giturl}|${htmlurl}"
  }
}
{{/groovy}}
