= Imported Repositories =

{{velocity}}
#if ($hasAdmin)
  [[Import Repositories>>GitHubCode.ImportRepositories]]
#end
{{/velocity}}

{{groovy}}
def repoData = xwiki.queryManager.xwql("select distinct repo.organization, repo.id, repo.name, repo.giturl, repo.htmlurl from Document doc, doc.object(GitHubCode.RepositoryClass) as repo").execute()
if (repoData.size() > 0) {
  println "|=Organization|=Id|=Name|=Git URL|=HTML URL"
  repoData.each() {
    println "|${it[0]}|${it[1]}|${it[2]}|${it[3]}|${it[4]}"
  }
  println ""
}
{{/groovy}}

= Imported Committers =

{{velocity}}
#if ($hasAdmin)
  [[Import Committers>>GitHubCode.ImportCommitters]]
#end
{{/velocity}}

{{groovy}}
def committerData = xwiki.queryManager.xwql("select distinct committer.avatar, committer.id, committer.repositories, committer.aliases from Document doc, doc.object(GitHubCode.CommitterClass) as committer").execute()
if (committerData.size() > 0) {
  println "|=Avatar|=Id|=Repositories|=Aliases"
  committerData.each() {
    println "|[[image:${it[0]}]]|${it[1]}|${it[2].tokenize('|').join(', ')}|${it[3].tokenize('|').join(', ')}"
  }
}
{{/groovy}}
